    private static getFromCacheOrCreate(key: string, data: string): HTMLImageElement {
        // return document.createElement('img');
        let eCachedImg: HTMLImageElement = this.imageCache[key];
        if (!eCachedImg) {
            eCachedImg = document.createElement('img');
            eCachedImg.src = data;
            this.imageCache[key] = eCachedImg;
        }
        return <HTMLImageElement> eCachedImg.cloneNode();
    }

